

print 'WORKFLOWS Contracts Notifications';
GO

set nocount on;
GO

declare @ID uniqueidentifier;
set @ID = '7EF768BA-E619-413F-9CC1-36CE8DFFF96C';
if not exists(select * from WORKFLOW where ID = @ID) begin -- then
	-- 07/19/2010 Paul.  Add PARENT_ID. 
	exec dbo.spWORKFLOWS_Update @ID, null, 'Contracts Notification', 'Contracts', 'CONTRACTS_AUDIT', 1, 'Normal', 'alerts_actions', 'All', null, 'select *
  from            CONTRACTS_AUDIT        CONTRACTS
  left outer join CONTRACTS_AUDIT        CONTRACTS_AUDIT_OLD
               on CONTRACTS_AUDIT_OLD.ID = CONTRACTS.ID
              and CONTRACTS_AUDIT_OLD.AUDIT_VERSION = (select max(CONTRACTS_AUDIT.AUDIT_VERSION)
                                                  from CONTRACTS_AUDIT
                                                 where CONTRACTS_AUDIT.ID            =  CONTRACTS.ID
                                                   and CONTRACTS_AUDIT.AUDIT_VERSION <  CONTRACTS.AUDIT_VERSION
                                                   and CONTRACTS_AUDIT.AUDIT_TOKEN   <> CONTRACTS.AUDIT_TOKEN
                                               )
  left outer join vwUSERS                     USERS_ASSIGNED_USER_ID
               on USERS_ASSIGNED_USER_ID.ID = CONTRACTS.ASSIGNED_USER_ID
 where CONTRACTS.AUDIT_ID = @AUDIT_ID
   and CONTRACTS.DELETED  = 0
   and (CONTRACTS_AUDIT_OLD.AUDIT_ID is null      or (not(CONTRACTS.ASSIGNED_USER_ID          is null     and CONTRACTS_AUDIT_OLD.ASSIGNED_USER_ID          is null    ) and (CONTRACTS.ASSIGNED_USER_ID          <> CONTRACTS_AUDIT_OLD.ASSIGNED_USER_ID          or CONTRACTS.ASSIGNED_USER_ID is null or CONTRACTS_AUDIT_OLD.ASSIGNED_USER_ID is null)))
   and USERS_ASSIGNED_USER_ID.RECEIVE_NOTIFICATIONS = 1
   and USERS_ASSIGNED_USER_ID.EMAIL1 is not null
