

print 'WORKFLOWS Invoices Notifications';
GO

set nocount on;
GO

declare @ID uniqueidentifier;
set @ID = '56C4E23B-4501-4BDE-97AA-293ADC629C60';
if not exists(select * from WORKFLOW where ID = @ID) begin -- then
	-- 07/19/2010 Paul.  Add PARENT_ID. 
	exec dbo.spWORKFLOWS_Update @ID, null, 'Invoices Notification', 'Invoices', 'INVOICES_AUDIT', 1, 'Normal', 'alerts_actions', 'All', null, 'select *
  from            INVOICES_AUDIT        INVOICES
  left outer join INVOICES_AUDIT        INVOICES_AUDIT_OLD
               on INVOICES_AUDIT_OLD.ID = INVOICES.ID
              and INVOICES_AUDIT_OLD.AUDIT_VERSION = (select max(INVOICES_AUDIT.AUDIT_VERSION)
                                                  from INVOICES_AUDIT
                                                 where INVOICES_AUDIT.ID            =  INVOICES.ID
                                                   and INVOICES_AUDIT.AUDIT_VERSION <  INVOICES.AUDIT_VERSION
                                                   and INVOICES_AUDIT.AUDIT_TOKEN   <> INVOICES.AUDIT_TOKEN
                                               )
  left outer join vwUSERS                     USERS_ASSIGNED_USER_ID
               on USERS_ASSIGNED_USER_ID.ID = INVOICES.ASSIGNED_USER_ID
 where INVOICES.AUDIT_ID = @AUDIT_ID
   and INVOICES.DELETED  = 0
   and (INVOICES_AUDIT_OLD.AUDIT_ID is null      or (not(INVOICES.ASSIGNED_USER_ID          is null     and INVOICES_AUDIT_OLD.ASSIGNED_USER_ID          is null    ) and (INVOICES.ASSIGNED_USER_ID          <> INVOICES_AUDIT_OLD.ASSIGNED_USER_ID          or INVOICES.ASSIGNED_USER_ID is null or INVOICES_AUDIT_OLD.ASSIGNED_USER_ID is null)))
   and USERS_ASSIGNED_USER_ID.RECEIVE_NOTIFICATIONS = 1
   and USERS_ASSIGNED_USER_ID.EMAIL1 is not null
', '<?xml version="1.0" encoding="UTF-8"?><Report Name="" xmlns:rd="http://schemas.microsoft.com/SQLServer/reporting/reportdesigner" xmlns="http://schemas.microsoft.com/sqlserver/reporting/2005/01/reportdefinition"><CustomProperties><CustomProperty><Name>crm:Module</Name><Value>Invoices</Value></CustomProperty><CustomProperty><Name>crm:Related</Name><Value></Value></CustomProperty><CustomProperty><Name>crm:RelatedModules</Name><Value>&lt;Relationships&gt;&lt;Relationship&gt;&lt;RELATIONSHIP_NAME&gt;invoices_contacts_billto&lt;/RELATIONSHIP_NAME&gt;&lt;LHS_MODULE&gt;Invoices&lt;/LHS_MODULE&gt;&lt;LHS_TABLE&gt;INVOICES&lt;/LHS_TABLE&gt;&lt;LHS_KEY&gt;ID&lt;/LHS_KEY&gt;&lt;RHS_MODULE&gt;Contacts&lt;/RHS_MODULE&gt;&lt;RHS_TABLE&gt;CONTACTS&lt;/RHS_TABLE&gt;&lt;RHS_KEY&gt;ID&lt;/RHS_KEY&gt;&lt;JOIN_TABLE&gt;INVOICES_CONTACTS&lt;/JOIN_TABLE&gt;&lt;JOIN_KEY_LHS&gt;INVOICE_ID&lt;/JOIN_KEY_LHS&gt;&lt;JOIN_KEY_RHS&gt;CONTACT_ID&lt;/JOIN_KEY_RHS&gt;&lt;RELATIONSHIP_TYPE&gt;many-to-many&lt;/RELATIONSHIP_TYPE&gt;&lt;MODULE_NAME&gt;Contacts CONTACTS&lt;/MODULE_NAME&gt;&lt;DISPLAY_NAME&gt;[Contacts CONTACTS] Contacts&lt;/DISPLAY_NAME&gt;&lt;/Relationship&gt;  &lt;Relationship&gt;&lt;RELATIONSHIP_NAME&gt;invoices_shipto_accounts&lt;/RELATIONSHIP_NAME&gt;&lt;LHS_MODULE&gt;Invoices&lt;/LHS_MODULE&gt;&lt;LHS_TABLE&gt;INVOICES&lt;/LHS_TABLE&gt;&lt;LHS_KEY&gt;ID&lt;/LHS_KEY&gt;&lt;RHS_MODULE&gt;Accounts&lt;/RHS_MODULE&gt;&lt;RHS_TABLE&gt;ACCOUNTS&lt;/RHS_TABLE&gt;&lt;RHS_KEY&gt;ID&lt;/RHS_KEY&gt;&lt;JOIN_TABLE&gt;INVOICES_ACCOUNTS&lt;/JOIN_TABLE&gt;&lt;JOIN_KEY_LHS&gt;INVOICE_ID&lt;/JOIN_KEY_LHS&gt;&lt;JOIN_KEY_RHS&gt;ACCOUNT_ID&lt;/JOIN_KEY_RHS&gt;&lt;RELATIONSHIP_TYPE&gt;many-to-many&lt;/RELATIONSHIP_TYPE&gt;&lt;MODULE_NAME&gt;Accounts ACCOUNTS&lt;/MODULE_NAME&gt;&lt;DISPLAY_NAME&gt;[Accounts ACCOUNTS] Accounts&lt;/DISPLAY_NAME&gt;&lt;/Relationship&gt;  &lt;Relationship&gt;&lt;RELATIONSHIP_NAME&gt;invoices_billto_accounts&lt;/RELATIONSHIP_NAME&gt;&lt;LHS_MODULE&gt;Invoices&lt;/LHS_MOD