, '<crm:SplendidSequentialWorkflow x:Name="Workflow1" WORKFLOW_ID="56C4E23B-4501-4BDE-97AA-293ADC629C60" xmlns:crm="clr-namespace:SplendidCRM;Assembly=SplendidCRM" xmlns:x="http://schemas.microsoft.com/winfx/2006/xaml" xmlns="http://schemas.microsoft.com/winfx/2006/xaml/workflow">
	<crm:InvoiceActivity x:Name="Invoices2" WORKFLOW_ID="{ActivityBind Workflow1,Path=WORKFLOW_ID}" AUDIT_ID="{ActivityBind Workflow1,Path=AUDIT_ID}" xmlns:x="http://schemas.microsoft.com/winfx/2006/xaml/workflow">
		<crm:WorkflowLogActivity x:Name="Invoices2_Log" MODULE_TABLE="INVOICES" WORKFLOW_ID="{ActivityBind Workflow1,Path=WORKFLOW_ID}" />

		<CodeActivity x:Name="Invoices2_LoadByAUDIT_ID" ExecuteCode="{ActivityBind Invoices2,Path=LoadByAUDIT_ID}" />
	</crm:InvoiceActivity>
	<crm:AlertActivity x:Name="Alert2" SUBJECT="SplendidCRM Invoice - $invoice_name" ALERT_TYPE="Notification" ALERT_TEXT="$invoice_modified_by has assigned an Invoice to $invoice_assigned_to.&#xD;&#xA;&#xD;&#xA;Invoice Name: $invoice_name&#xD;&#xA;&#xD;&#xA;Invoice Number: $invoice_invoice_num&#xD;&#xA;&#xD;&#xA;Invoice Stage: $invoice_invoice_stage&#xD;&#xA;&#xD;&#xA;Due Date: $invoice_due_date&#xD;&#xA;&#xD;&#xA;Description: $invoice_description&#xD;&#xA;&#xD;&#xA;You may review this Invoice at:&#xD;&#xA;&lt;a href=&quot;$view_url&quot;&gt;$view_url&lt;/a&gt;&#xD;&#xA;&#xD;&#xA;" SOURCE_TYPE="normal message" CUSTOM_TEMPLATE_ID="00000000-0000-0000-0000-000000000000" PARENT_TYPE="Invoices" PARENT_ACTIVITY="Invoices2" WORKFLOW_ID="{ActivityBind Workflow1,Path=WORKFLOW_ID}" xmlns:x="http://schemas.microsoft.com/winfx/2006/xaml/workflow">
		<crm:WorkflowLogActivity x:Name="Alert2_Log" MODULE_TABLE="EMAILS" WORKFLOW_ID="{ActivityBind Workflow1,Path=WORKFLOW_ID}" />
		<crm:AddRecipientActivity x:Name="Alert2_Invoices2_AssignedUserID" ALERT_NAME="Alert2" RECIPIENT_NAME="" RECIPIENT_ID="{ActivityBind Invoices2,Path=ASSIGNED_USER_ID}" RECIPIENT_TYPE="User" SEND_TYPE="to" />

		<CodeActivity x:Name="Alert2_Send" ExecuteCode="{ActivityBind Alert2,Path=Send}" />
	</crm:AlertActivity>
</crm:SplendidSequentialWorkflow>';

	exec dbo.spWORKFLOW_TRIGGER_SHELLS_InsertOnly null, @ID, 'RECEIVE_NOTIFICATIONS', 'compare_specific', 'Primary', 'equals'   , 0, 'Users'   , null, '1';
	exec dbo.spWORKFLOW_TRIGGER_SHELLS_InsertOnly null, @ID, 'EMAIL1'               , 'compare_specific', 'Primary', 'not_empty', 0, 'Users'   , null, null;
	exec dbo.spWORKFLOW_TRIGGER_SHELLS_InsertOnly null, @ID, 'ASSIGNED_USER_ID'     , 'compare_change'  , 'Primary', 'changed'  , 0, 'Invoices', null, null;
end -- if;
GO


set nocount off;
GO

