

print 'WORKFLOWS Leads Notifications';
GO

set nocount on;
GO

declare @ID uniqueidentifier;
set @ID = '10D4A1DB-D4E7-4B02-A68C-1032D44ED6F9';
if not exists(select * from WORKFLOW where ID = @ID) begin -- then
	-- 07/19/2010 Paul.  Add PARENT_ID. 
	exec dbo.spWORKFLOWS_Update @ID, null, 'Leads Notification', 'Leads', 'LEADS_AUDIT', 1, 'Normal', 'alerts_actions', 'All', null, 'select *
  from            LEADS_AUDIT        LEADS
  left outer join LEADS_AUDIT        LEADS_AUDIT_OLD
               on LEADS_AUDIT_OLD.ID = LEADS.ID
              and LEADS_AUDIT_OLD.AUDIT_VERSION = (select max(LEADS_AUDIT.AUDIT_VERSION)
                                                  from LEADS_AUDIT
                                                 where LEADS_AUDIT.ID            =  LEADS.ID
                                                   and LEADS_AUDIT.AUDIT_VERSION <  LEADS.AUDIT_VERSION
                                                   and LEADS_AUDIT.AUDIT_TOKEN   <> LEADS.AUDIT_TOKEN
                                               )
  left outer join vwUSERS                     USERS_ASSIGNED_USER_ID
               on USERS_ASSIGNED_USER_ID.ID = LEADS.ASSIGNED_USER_ID
 where LEADS.AUDIT_ID = @AUDIT_ID
   and LEADS.DELETED  = 0
   and (LEADS_AUDIT_OLD.AUDIT_ID is null      or (not(LEADS.ASSIGNED_USER_ID          is null     and LEADS_AUDIT_OLD.ASSIGNED_USER_ID          is null    ) and (LEADS.ASSIGNED_USER_ID          <> LEADS_AUDIT_OLD.ASSIGNED_USER_ID          or LEADS.ASSIGNED_USER_ID is null or LEADS_AUDIT_OLD.ASSIGNED_USER_ID is null)))
   and USERS_ASSIGNED_USER_ID.RECEIVE_NOTIFICATIONS = 1
   and USERS_ASSIGNED_USER_ID.EMAIL1 is not null
