t>', null;

	exec dbo.spWORKFLOWS_UpdateXOML @ID, null, 0, '<crm:SplendidSequentialWorkflow x:Name="Workflow1" WORKFLOW_ID="10D4A1DB-D4E7-4B02-A68C-1032D44ED6F9" xmlns:crm="clr-namespace:SplendidCRM;Assembly=SplendidCRM" xmlns:x="http://schemas.microsoft.com/winfx/2006/xaml" xmlns="http://schemas.microsoft.com/winfx/2006/xaml/workflow">
	<SequenceActivity x:Name="Sequence1" xmlns:x="http://schemas.microsoft.com/winfx/2006/xaml/workflow">
		<crm:LeadActivity x:Name="Leads1" WORKFLOW_ID="{ActivityBind Workflow1,Path=WORKFLOW_ID}" AUDIT_ID="{ActivityBind Workflow1,Path=AUDIT_ID}">
			<crm:WorkflowLogActivity x:Name="Leads1_Log" MODULE_TABLE="LEADS" WORKFLOW_ID="{ActivityBind Workflow1,Path=WORKFLOW_ID}" />

			<CodeActivity x:Name="Leads1_LoadByAUDIT_ID" ExecuteCode="{ActivityBind Leads1,Path=LoadByAUDIT_ID}" />
		</crm:LeadActivity>
		<crm:AlertActivity x:Name="Alert1" SUBJECT="SplendidCRM Lead - $lead_name" ALERT_TYPE="Notification" ALERT_TEXT="$lead_modified_by has assigned an Lead to $lead_assigned_to.&#xD;&#xA;&#xD;&#xA;Name: $lead_name&#xD;&#xA;&#xD;&#xA;Lead Source: $lead_lead_source&#xD;&#xA;&#xD;&#xA;Status: $lead_status&#xD;&#xA;&#xD;&#xA;Description: $lead_description&#xD;&#xA;&#xD;&#xA;You may review this Lead at:&#xD;&#xA;&lt;a href=&quot;$view_url&quot;&gt;$view_url&lt;/a&gt;&#xD;&#xA;&#xD;&#xA;" SOURCE_TYPE="normal message" CUSTOM_TEMPLATE_ID="00000000-0000-0000-0000-000000000000" PARENT_TYPE="Leads" PARENT_ACTIVITY="Leads1" WORKFLOW_ID="{ActivityBind Workflow1,Path=WORKFLOW_ID}">
			<crm:WorkflowLogActivity x:Name="Alert1_Log" MODULE_TABLE="EMAILS" WORKFLOW_ID="{ActivityBind Workflow1,Path=WORKFLOW_ID}" />
			<crm:AddRecipientActivity x:Name="Alert1_Leads1_AssignedUserID" ALERT_NAME="Alert1" RECIPIENT_NAME="" RECIPIENT_ID="{ActivityBind Leads1,Path=ASSIGNED_USER_ID}" RECIPIENT_TYPE="User" SEND_TYPE="to" />

			<CodeActivity x:Name="Alert1_Send" ExecuteCode="{ActivityBind Alert1,Path=Send}" />
		</crm:AlertActivity>
	</SequenceActivity>
</crm:SplendidSequentialWorkflow>';

