

print 'WORKFLOWS Opportunities Notifications';
GO

set nocount on;
GO

declare @ID uniqueidentifier;
set @ID = 'E6652948-258F-4557-BD8B-4C9CBB51FDD4';
if not exists(select * from WORKFLOW where ID = @ID) begin -- then
	-- 07/19/2010 Paul.  Add PARENT_ID. 
	exec dbo.spWORKFLOWS_Update @ID, null, 'Opportunities Notification', 'Opportunities', 'OPPORTUNITIES_AUDIT', 1, 'Normal', 'alerts_actions', 'All', null, 'select *
  from            OPPORTUNITIES_AUDIT        OPPORTUNITIES
  left outer join OPPORTUNITIES_AUDIT        OPPORTUNITIES_AUDIT_OLD
               on OPPORTUNITIES_AUDIT_OLD.ID = OPPORTUNITIES.ID
              and OPPORTUNITIES_AUDIT_OLD.AUDIT_VERSION = (select max(OPPORTUNITIES_AUDIT.AUDIT_VERSION)
                                                  from OPPORTUNITIES_AUDIT
                                                 where OPPORTUNITIES_AUDIT.ID            =  OPPORTUNITIES.ID
                                                   and OPPORTUNITIES_AUDIT.AUDIT_VERSION <  OPPORTUNITIES.AUDIT_VERSION
                                                   and OPPORTUNITIES_AUDIT.AUDIT_TOKEN   <> OPPORTUNITIES.AUDIT_TOKEN
                                               )
  left outer join vwUSERS                     USERS_ASSIGNED_USER_ID
               on USERS_ASSIGNED_USER_ID.ID = OPPORTUNITIES.ASSIGNED_USER_ID
 where OPPORTUNITIES.AUDIT_ID = @AUDIT_ID
   and OPPORTUNITIES.DELETED  = 0
   and (OPPORTUNITIES_AUDIT_OLD.AUDIT_ID is null      or (not(OPPORTUNITIES.ASSIGNED_USER_ID          is null     and OPPORTUNITIES_AUDIT_OLD.ASSIGNED_USER_ID          is null    ) and (OPPORTUNITIES.ASSIGNED_USER_ID          <> OPPORTUNITIES_AUDIT_OLD.ASSIGNED_USER_ID          or OPPORTUNITIES.ASSIGNED_USER_ID is null or OPPORTUNITIES_AUDIT_OLD.ASSIGNED_USER_ID is null)))
   and USERS_ASSIGNED_USER_ID.RECEIVE_NOTIFICATIONS = 1
   and USERS_ASSIGNED_USER_ID.EMAIL1 is not null
', '<?xml version="1.0" encoding="UTF-8"?><Report Name="" xmlns:rd="http://schemas.microsoft.com/SQLServer/reporting/reportdesigner" xmlns="http://schemas.microsoft.com/sqlserver/reporting/2005/01/reportdefinition"><CustomProperties><CustomProperty><Name>crm:Module</Name><Value>Opportunities</Value></CustomProperty><CustomProperty><Name>crm:Related</Name><Value></Value></CustomProperty><CustomProperty><Name>crm:RelatedModules</Name><Value>&lt;Relationships&gt;&lt;Relationship&gt;&lt;RELATIONSHIP_NAME&gt;opportunities_contacts&lt;/RELATIONSHIP_NAME&gt;&lt;LHS_MODULE&gt;Opportunities&lt;/LHS_MODULE&gt;&lt;LHS_TABLE&gt;OPPORTUNITIES&lt;/LHS_TABLE&gt;&lt;LHS_KEY&gt;ID&lt;/LHS_KEY&gt;&lt;RHS_MODULE&gt;Contacts&lt;/RHS_MODULE&gt;&lt;RHS_TABLE&gt;CONTACTS&lt;/RHS_TABLE&gt;&lt;RHS_KEY&gt;ID&lt;/RHS_KEY&gt;&lt;JOIN_TABLE&gt;OPPORTUNITIES_CONTACTS&lt;/JOIN_TABLE&gt;&lt;JOIN_KEY_LHS&gt;OPPORTUNITY_ID&lt;/JOIN_KEY_LHS&gt;&lt;JOIN_KEY_RHS&gt;CONTACT_ID&lt;/JOIN_KEY_RHS&gt;&lt;RELATIONSHIP_TYPE&gt;many-to-many&lt;/RELATIONSHIP_TYPE&gt;&lt;MODULE_NAME&gt;Contacts CONTACTS&lt;/MODULE_NAME&gt;&lt;DISPLAY_NAME&gt;[Contacts CONTACTS] Contacts&lt;/DISPLAY_NAME&gt;&lt;/Relationship&gt;  &lt;/Relationships&gt;</Value></CustomProperty><CustomProperty><Name>crm:Relationships</Name><Value>&lt;Relationships&gt;&lt;Relationship&gt;&lt;RELATIONSHIP_NAME&gt;Opportunities&lt;/RELATIONSHIP_NAME&gt;&lt;LHS_MODULE&gt;Opportunities&lt;/LHS_MODULE&gt;&lt;LHS_TABLE&gt;OPPORTUNITIES&lt;/LHS_TABLE&gt;&lt;LHS_KEY&gt;&lt;/LHS_KEY&gt;&lt;RHS_MODULE&gt;&lt;/RHS_MODULE&gt;&lt;RHS_TABLE&gt;&lt;/RHS_TABLE&gt;&lt;RHS_KEY&gt;&lt;/RHS_KEY&gt;&lt;RELATIONSHIP_TYPE&gt;&lt;/RELATIONSHIP_TYPE&gt;&lt;MODULE_ALIAS&gt;OPPORTUNITIES&lt;/MODULE_ALIAS&gt;&lt;MODULE_NAME&gt;Opportunities OPPORTUNITIES&lt;/MODULE_NAME&gt;&lt;DISPLAY_NAME&gt;[Opportunities OPPORTUNITIES] Opportunities&lt;/DISPLAY_NAME&gt;&lt;/Relationship&gt;  &lt;Relationship&gt;&lt;RELATIONSHIP_NAME&gt;opportunities_assigned_user&lt;/RELATIONSHIP_NAME&gt;&lt;LHS_MODULE&gt;Users&lt;/LHS_MODULE&gt;&lt;LHS_TABLE&gt;USERS&lt;/LHS_TABLE&gt;&lt;LHS_KEY&gt;ID&lt;/LHS_KEY&gt;&lt;RHS_MODULE&gt;Opportunities&lt;/RHS_MODULE&gt;&lt;RHS_TABLE&gt;OPPORTUNITIES&lt;/RHS_TABLE&gt;&lt;RHS_KEY&gt;ASSIGNED_USER_ID&lt;/RHS_KEY&gt;&lt;RELATIONSHIP_TYPE&gt;one-to-many&lt;/RELATIONSHIP_TYPE&gt;&lt;MODULE_ALIAS&gt;USERS_ASSIGNED_USER_ID&lt;/MODULE_ALIAS&gt;&lt;MODULE_NAME&gt;Users USERS_ASSIGNED_USER_ID&lt;/MODULE_NAME&gt;&lt;DISPLAY_NAME&gt;[Users USERS_ASSIGNED_USER_ID] Opportunities: Assigned to User&lt;/DISPLAY_NAME&gt;&lt;/Relationship&gt;  &lt;Relationship&gt;&lt;RELATIONSHIP_NAME&gt;opportunities_created_by&lt;/RELATIONSHIP_NAME&gt;&lt;LHS_MODULE&gt;Users&lt;/LHS_MODULE&gt;&lt;LHS_TABLE&gt;USERS&lt;/LHS_TABLE&gt;&lt;LHS_KEY&gt;ID&lt;/LHS_KEY&gt;&lt;RHS_MODULE&gt;Opportunities&lt;/RHS_MODULE&gt;&lt;RHS_TABLE&gt;OPPORTUNITIES&lt;/RHS_TABLE&gt;&lt;RHS_KEY&gt;CREATED_BY_ID&lt;/RHS_KEY&gt;&lt;RELATIONSHIP_TYPE&gt;one-to-many&lt;/RELATIONSHIP_TYPE&gt;&lt;MODULE_ALIAS&gt;USERS_CREATED_BY_ID&lt;/MODULE_ALIAS&gt;&lt;MODULE_NAME&gt;Users USERS_CREATED_BY_ID&lt;/MODULE_NAME&gt;&lt;DISPLAY_NAME&gt;[Users USERS_CREATED_BY_ID] Opportunities: Created by User&lt;/DISPLAY_NAME&gt;&lt;/Relationship&gt;  &lt;Relationship&gt;&lt;RELATIONSHIP_NAME&gt;opportunities_modified_user&lt;/RELATIONSHIP_NAME&gt;&lt;LHS_MODULE&gt;Users&lt;/LHS_MODULE&gt;&lt;LHS_TABLE&gt;USERS&lt;/LHS_TABLE&gt;&lt;LHS_KEY&gt;ID&lt;/LHS_KEY&gt;&lt;RHS_MODULE&gt;Opportunities&lt;/RHS_MODULE&gt;&lt;RHS_TABLE&gt;OPPORTUNITIES&lt;/RHS_TABLE&gt;&lt;RHS_KEY&gt;MODIFIED_USER_ID&lt;/RHS_KEY&gt;&lt;RELATIONSHIP_TYPE&gt;one-to-many&lt;/RELATIONSHIP_TYPE&gt;&lt;MODULE_ALIAS&gt;USERS_MODIFIED_USER_ID&lt;/MODULE_ALIAS&gt;&lt;MODULE_NAME&gt;Users USERS_MODIFIED_USER_ID&lt;/MODULE_NAME&gt;&lt;DISPLAY_NAME&gt;[Users USERS_MODIFIED_USER_ID] Opportunities: Modified by User&lt;/DISPLAY_NAME&gt;&lt;/Relationship&gt;  &lt;Relationship&gt;&lt;RELATIONSHIP_NAME&gt;opportunities_audit_old&lt;/RELATIONSHIP_NAME&gt;&lt;LHS_MODULE&gt;Opportunities&lt;/LHS_MODULE&gt;&lt;LHS_TABLE&gt;OPPORTUNITIES_AUDIT&lt;/LHS_TABLE&gt;&lt;LHS_KEY&gt;ID&lt;/LHS_KEY&gt;&lt;RHS_MODULE&gt;Opportunities&lt;/RHS_MODULE&gt;&lt;RHS_TABLE&gt;OPPORTUNITIES&lt;/RHS_TABLE&gt;&lt;RHS_KEY&gt;ID&lt;/RHS_KEY&gt;&lt;RELATIONSHIP_TYPE&gt;&lt;/RELATIONSHIP_TYPE&gt;&lt;MODULE_ALIAS&gt;OPPORTUNITIES_AUDIT_OLD&lt;/MODULE_ALIAS&gt;&lt;MODULE_NAME&gt;Opportunities OPPORTUNITIES_AUDIT_OLD&lt;/MODULE_NAME&gt;&lt;DISPLAY_NAME&gt;[Opportunities OPPORTUNITIES_AUDIT_OLD] Opportunities: Audit Old&lt;/DISPLAY_NAME&gt;&lt;/Relationship&gt;  &lt;/Relationships&gt;</Value></CustomProperty><CustomProperty><Name>crm:Filters</Name><Value>&lt;Filters&gt;&lt;Filter&gt;&lt;ID&gt;6418ef68-c77c-4788-99c3-24e1965f55ac&lt;/ID&gt;&lt;MODULE&gt;Opportunities&lt;/MODULE&gt;&lt;MODULE_NAME&gt;Opportunities OPPORTUNITIES_AUDIT_OLD&lt;/MODULE_NAME&gt;&lt;TABLE_NAME&gt;OPPORTUNITIES_AUDIT_OLD&lt;/TABLE_NAME&gt;&lt;DATA_FIELD&gt;OPPORTUNITIES_AUDIT_OLD.ASSIGNED_USER_ID&lt;/DATA_FIELD&gt;&lt;FIELD_NAME&gt;ASSIGNED_USER_ID&lt;/FIELD_NAME&gt;&lt;DATA_TYPE&gt;guid&lt;/DATA_TYPE&gt;&lt;OPERATOR&gt;changed&lt;/OPERATOR&gt;&lt;SEARCH_TEXT&gt;&lt;/SEARCH_TEXT&gt;&lt;/Filter&gt;&lt;Filter&gt;&lt;ID&gt;084780ff-bbac-45ed-942f-fa0220ad5ad2&lt;/ID&gt;&lt;MODULE&gt;Users&lt;/MODULE&gt;&lt;MODULE_NAME&gt;Users USERS_ASSIGNED_USER_ID&lt;/MODULE_NAME&gt;&lt;TABLE_NAME&gt;USERS_ASSIGNED_USER_ID&lt;/TABLE_NAME&gt;&lt;DATA_FIELD&gt;USERS_ASSIGNED_USER_ID.RECEIVE_NOTIFICATIONS&lt;/DATA_FIELD&gt;&lt;FIELD_NAME&gt;RECEIVE_NOTIFICATIONS&lt;/FIELD_NAME&gt;&lt;DATA_TYPE&gt;bool&lt;/DATA_TYPE&gt;&lt;OPERATOR&gt;equals&lt;/OPERATOR&gt;&lt;SEARCH_TEXT&gt;1&lt;/SEARCH_TEXT&gt;&lt;SEARCH_TEXT_VALUES&gt;1&lt;/SEARCH_TEXT_VALUES&gt;&lt;/Filter&gt;&lt;Filter&gt;&lt;ID&gt;729c9746-c197-43fc-a3b4-d1e38cf28a58&lt;/ID&gt;&lt;MODULE&gt;Users&lt;/MODULE&gt;&lt;MODULE_NAME&gt;Users USERS_ASSIGNED_USER_ID&lt;/MODULE_NAME&gt;&lt;TABLE_NAME&gt;USERS_ASSIGNED_USER_ID&lt;/TABLE_NAME&gt;&lt;DATA_FIELD&gt;USERS_ASSIGNED_USER_ID.EMAIL1&lt;/DATA_FIELD&gt;&lt;FIELD_NAME&gt;EMAIL1&lt;/FIELD_NAME&gt;&lt;DATA_TYPE&gt;string&lt;/DATA_TYPE&gt;&lt;OPERATOR&gt;not_empty&lt;/OPERATOR&gt;&lt;SEARCH_TEXT&gt;&lt;/SEARCH_TEXT&gt;&lt;/Filter&gt;&lt;/Filters&gt;</Value></CustomProperty></CustomProperties><DataSets><DataSet><Query><CommandText>select *    from            OPPORTUNITIES_AUDIT   OPPORTUNITIES    left outer join OPPORTUNITIES_AUDIT        OPPORTUNITIES_AUDIT_OLD                 on OPPORTUNITIES_AUDIT_OLD.ID = OPPORTUNITIES.ID                and OPPORTUNITIES_AUDIT_OLD.AUDIT_VERSION = (select max(OPPORTUNITIES_AUDIT.AUDIT_VERSION)                                                    from OPPORTUNITIES_AUDIT                                                   where OPPORTUNITIES_AUDIT.ID            =  OPPORTUNITIES.ID                                                     and OPPORTUNITIES_AUDIT.AUDIT_VERSION &lt;  OPPORTUNITIES.AUDIT_VERSION                                                     and OPPORTUNITIES_AUDIT.AUDIT_TOKEN   &lt;&gt; OPPORTUNITIES.AUDIT_TOKEN                                                 )    left outer join vwUSERS                      USERS_ASSIGNED_USER_ID                 on USERS_ASSIGNED_USER_ID.ID  = OPPORTUNITIES.ASSIGNED_USER_ID   where OPPORTUNITIES.AUDIT_ID = @AUDIT_ID     and OPPORTUNITIES.DELETED  = 0     and (OPPORTUNITIES_AUDIT_OLD.AUDIT_ID is null      or (not(OPPORTUNITIES.ASSIGNED_USER_ID     is null     and OPPORTUNITIES_AUDIT_OLD.ASSIGNED_USER_ID     is null    ) and (OPPORTUNITIES.ASSIGNED_USER_ID     &lt;&gt; OPPORTUNITIES_AUDIT_OLD.ASSIGNED_USER_ID     or OPPORTUNITIES.ASSIGNED_USER_ID is null or OPPORTUNITIES_AUDIT_OLD.ASSIGNED_USER_ID is null)))     and USERS_ASSIGNED_USER_ID.RECEIVE_NOTIFICATIONS = 1     and USERS_ASSIGNED_USER_ID.EMAIL1 is not null  </CommandText></Query><Fields></Fields></DataSet></DataSets></Report>', null;

	exec dbo.spWORKFLOWS_UpdateXOML @ID, null, 0, '<crm:SplendidSequentialWorkflow x:Name="Workflow1" WORKFLOW_ID="E6652948-258F-4557-BD8B-4C9CBB51FDD4" xmlns:crm="clr-namespace:SplendidCRM;Assembly=SplendidCRM" xmlns:x="http://schemas.microsoft.com/winfx/2006/xaml" xmlns="http://schemas.microsoft.com/winfx/2006/xaml/workflow">
	<SequenceActivity x:Name="Sequence1" xmlns:x="http://schemas.microsoft.com/winfx/2006/xaml/workflow">
		<crm:OpportunityActivity x:Name="Opportunities1" WORKFLOW_ID="{ActivityBind Workflow1,Path=WORKFLOW_ID}" AUDIT_ID="{ActivityBind Workflow1,Path=AUDIT_ID}">
			<crm:WorkflowLogActivity x:Name="Opportunities1_Log" MODULE_TABLE="OPPORTUNITIES" WORKFLOW_ID="{ActivityBind Workflow1,Path=WORKFLOW_ID}" />

			<CodeActivity x:Name="Opportunities1_LoadByAUDIT_ID" ExecuteCode="{ActivityBind Opportunities1,Path=LoadByAUDIT_ID}" />
		</crm:OpportunityActivity>
		<crm:AlertActivity x:Name="Alert1" SUBJECT="SplendidCRM Opportunity - $opportunity_name" ALERT_TYPE="Notification" ALERT_TEXT="$opportunity_modified_by has assigned an Opportunity to $opportunity_assigned_to.&#xD;&#xA;&#xD;&#xA;Name: $opportunity_name&#xD;&#xA;&#xD;&#xA;Amount: $opportunity_amount&#xD;&#xA;&#xD;&#xA;Expected Close Date: $opportunity_date_closed&#xD;&#xA;&#xD;&#xA;Sales Stage: $opportunity_sales_stage&#xD;&#xA;&#xD;&#xA;Description: $opportunity_description&#xD;&#xA;&#xD;&#xA;You may review this Opportunity at:&#xD;&#xA;&lt;a href=&quot;$view_url&quot;&gt;$view_url&lt;/a&gt;&#xD;&#xA;&#xD;&#xA;" SOURCE_TYPE="normal message" CUSTOM_TEMPLATE_ID="00000000-0000-0000-0000-000000000000" PARENT_TYPE="Opportunities" PARENT_ACTIVITY="Opportunities1" WORKFLOW_ID="{ActivityBind Workflow1,Path=WORKFLOW_ID}">
			<crm:WorkflowLogActivity x:Name="Alert1_Log" MODULE_TABLE="EMAILS" WORKFLOW_ID="{ActivityBind Workflow1,Path=WORKFLOW_ID}" />
			<crm:AddRecipientActivity x:Name="Alert1_Opportunities1_AssignedUserID" ALERT_NAME="Alert1" RECIPIENT_NAME="" RECIPIENT_ID="{ActivityBind Opportunities1,Path=ASSIGNED_USER_ID}" RECIPIENT_TYPE="User" SEND_TYPE="to" />

			<CodeActivity x:Name="Alert1_Send" ExecuteCode="{ActivityBind Alert1,Path=Send}" />
		</crm:AlertActivity>
	</SequenceActivity>
</crm:SplendidSequentialWorkflow>';

	exec dbo.spWORKFLOW_ALERT_SHELLS_InsertOnly null, @ID, 'SplendidCRM Opportunity - $opportunity_name', 'Email', 'normal message', null, '$opportunity_modified_by has assigned an Opportunity to $opportunity_assigned_to.

Name: $opportunity_name
Amount: $opportunity_amount
Expected Close Date: $opportunity_date_closed
Sales Stage: $opportunity_sales_stage
Description: $opportunity_description

You may review this Opportunity at:
<a href="$view_url">$view_url</a>

