, '<crm:SplendidSequentialWorkflow x:Name="Workflow1" WORKFLOW_ID="E6652948-258F-4557-BD8B-4C9CBB51FDD4" xmlns:crm="clr-namespace:SplendidCRM;Assembly=SplendidCRM" xmlns:x="http://schemas.microsoft.com/winfx/2006/xaml" xmlns="http://schemas.microsoft.com/winfx/2006/xaml/workflow">
	<crm:OpportunityActivity x:Name="Opportunities2" WORKFLOW_ID="{ActivityBind Workflow1,Path=WORKFLOW_ID}" AUDIT_ID="{ActivityBind Workflow1,Path=AUDIT_ID}" xmlns:x="http://schemas.microsoft.com/winfx/2006/xaml/workflow">
		<crm:WorkflowLogActivity x:Name="Opportunities2_Log" MODULE_TABLE="OPPORTUNITIES" WORKFLOW_ID="{ActivityBind Workflow1,Path=WORKFLOW_ID}" />

		<CodeActivity x:Name="Opportunities2_LoadByAUDIT_ID" ExecuteCode="{ActivityBind Opportunities2,Path=LoadByAUDIT_ID}" />
	</crm:OpportunityActivity>
	<crm:AlertActivity x:Name="Alert2" SUBJECT="SplendidCRM Opportunity - $opportunity_name" ALERT_TYPE="Notification" ALERT_TEXT="$opportunity_modified_by has assigned an Opportunity to $opportunity_assigned_to.&#xD;&#xA;&#xD;&#xA;Name: $opportunity_name&#xD;&#xA;&#xD;&#xA;Amount: $opportunity_amount&#xD;&#xA;&#xD;&#xA;Expected Close Date: $opportunity_date_closed&#xD;&#xA;&#xD;&#xA;Sales Stage: $opportunity_sales_stage&#xD;&#xA;&#xD;&#xA;Description: $opportunity_description&#xD;&#xA;&#xD;&#xA;You may review this Opportunity at:&#xD;&#xA;&lt;a href=&quot;$view_url&quot;&gt;$view_url&lt;/a&gt;&#xD;&#xA;&#xD;&#xA;" SOURCE_TYPE="normal message" CUSTOM_TEMPLATE_ID="00000000-0000-0000-0000-000000000000" PARENT_TYPE="Opportunities" PARENT_ACTIVITY="Opportunities2" WORKFLOW_ID="{ActivityBind Workflow1,Path=WORKFLOW_ID}" xmlns:x="http://schemas.microsoft.com/winfx/2006/xaml/workflow">
		<crm:WorkflowLogActivity x:Name="Alert2_Log" MODULE_TABLE="EMAILS" WORKFLOW_ID="{ActivityBind Workflow1,Path=WORKFLOW_ID}" />
		<crm:AddRecipientActivity x:Name="Alert2_Opportunities2_AssignedUserID" ALERT_NAME="Alert2" RECIPIENT_NAME="" RECIPIENT_ID="{ActivityBind Opportunities2,Path=ASSIGNED_USER_ID}" RECIPIENT_TYPE="User" SEND_TYPE="to" />

		<CodeActivity x:Name="Alert2_Send" ExecuteCode="{ActivityBind Alert2,Path=Send}" />
	</crm:AlertActivity>
</crm:SplendidSequentialWorkflow>';

	exec dbo.spWORKFLOW_TRIGGER_SHELLS_InsertOnly null, @ID, 'RECEIVE_NOTIFICATIONS', 'compare_specific', 'Primary', 'equals'   , 0, 'Users'   , null, '1';
	exec dbo.spWORKFLOW_TRIGGER_SHELLS_InsertOnly null, @ID, 'EMAIL1'               , 'compare_specific', 'Primary', 'not_empty', 0, 'Users'   , null, null;
	exec dbo.spWORKFLOW_TRIGGER_SHELLS_InsertOnly null, @ID, 'ASSIGNED_USER_ID'     , 'compare_change'  , 'Primary', 'changed'  , 0, 'Opportunities', null, null;
end -- if;
GO


