

print 'WORKFLOWS Tasks Notifications';
GO

set nocount on;
GO

declare @ID uniqueidentifier;
set @ID = '2D1019C7-BF57-40E8-878D-4A0D2BC7AF9B';
if not exists(select * from WORKFLOW where ID = @ID) begin -- then
	-- 07/19/2010 Paul.  Add PARENT_ID. 
	exec dbo.spWORKFLOWS_Update @ID, null, 'Tasks Notification', 'Tasks', 'TASKS_AUDIT', 1, 'Normal', 'alerts_actions', 'All', null, 'select *
  from            TASKS_AUDIT        TASKS
  left outer join TASKS_AUDIT        TASKS_AUDIT_OLD
               on TASKS_AUDIT_OLD.ID = TASKS.ID
              and TASKS_AUDIT_OLD.AUDIT_VERSION = (select max(TASKS_AUDIT.AUDIT_VERSION)
                                                  from TASKS_AUDIT
                                                 where TASKS_AUDIT.ID            =  TASKS.ID
                                                   and TASKS_AUDIT.AUDIT_VERSION <  TASKS.AUDIT_VERSION
                                                   and TASKS_AUDIT.AUDIT_TOKEN   <> TASKS.AUDIT_TOKEN
                                               )
  left outer join vwUSERS                     USERS_ASSIGNED_USER_ID
               on USERS_ASSIGNED_USER_ID.ID = TASKS.ASSIGNED_USER_ID
 where TASKS.AUDIT_ID = @AUDIT_ID
   and TASKS.DELETED  = 0
   and (TASKS_AUDIT_OLD.AUDIT_ID is null      or (not(TASKS.ASSIGNED_USER_ID          is null     and TASKS_AUDIT_OLD.ASSIGNED_USER_ID          is null    ) and (TASKS.ASSIGNED_USER_ID          <> TASKS_AUDIT_OLD.ASSIGNED_USER_ID          or TASKS.ASSIGNED_USER_ID is null or TASKS_AUDIT_OLD.ASSIGNED_USER_ID is null)))
   and USERS_ASSIGNED_USER_ID.RECEIVE_NOTIFICATIONS = 1
   and USERS_ASSIGNED_USER_ID.EMAIL1 is not null
