MODULE&gt;Users&lt;/LHS_MODULE&gt;&lt;LHS_TABLE&gt;USERS&lt;/LHS_TABLE&gt;&lt;LHS_KEY&gt;ID&lt;/LHS_KEY&gt;&lt;RHS_MODULE&gt;Quotes&lt;/RHS_MODULE&gt;&lt;RHS_TABLE&gt;QUOTES&lt;/RHS_TABLE&gt;&lt;RHS_KEY&gt;MODIFIED_USER_ID&lt;/RHS_KEY&gt;&lt;RELATIONSHIP_TYPE&gt;one-to-many&lt;/RELATIONSHIP_TYPE&gt;&lt;MODULE_ALIAS&gt;USERS_MODIFIED_USER_ID&lt;/MODULE_ALIAS&gt;&lt;MODULE_NAME&gt;Users USERS_MODIFIED_USER_ID&lt;/MODULE_NAME&gt;&lt;DISPLAY_NAME&gt;[Users USERS_MODIFIED_USER_ID] Quotes: Modified by User&lt;/DISPLAY_NAME&gt;&lt;/Relationship&gt;  &lt;Relationship&gt;&lt;RELATIONSHIP_NAME&gt;quotes_audit_old&lt;/RELATIONSHIP_NAME&gt;&lt;LHS_MODULE&gt;Quotes&lt;/LHS_MODULE&gt;&lt;LHS_TABLE&gt;QUOTES_AUDIT&lt;/LHS_TABLE&gt;&lt;LHS_KEY&gt;ID&lt;/LHS_KEY&gt;&lt;RHS_MODULE&gt;Quotes&lt;/RHS_MODULE&gt;&lt;RHS_TABLE&gt;QUOTES&lt;/RHS_TABLE&gt;&lt;RHS_KEY&gt;ID&lt;/RHS_KEY&gt;&lt;RELATIONSHIP_TYPE&gt;&lt;/RELATIONSHIP_TYPE&gt;&lt;MODULE_ALIAS&gt;QUOTES_AUDIT_OLD&lt;/MODULE_ALIAS&gt;&lt;MODULE_NAME&gt;Quotes QUOTES_AUDIT_OLD&lt;/MODULE_NAME&gt;&lt;DISPLAY_NAME&gt;[Quotes QUOTES_AUDIT_OLD] Quotes: Audit Old&lt;/DISPLAY_NAME&gt;&lt;/Relationship&gt;  &lt;/Relationships&gt;</Value></CustomProperty><CustomProperty><Name>crm:Filters</Name><Value>&lt;Filters&gt;&lt;Filter&gt;&lt;ID&gt;1131715f-c97d-4707-8c37-d42800e74c38&lt;/ID&gt;&lt;MODULE&gt;Quotes&lt;/MODULE&gt;&lt;MODULE_NAME&gt;Quotes QUOTES_AUDIT_OLD&lt;/MODULE_NAME&gt;&lt;TABLE_NAME&gt;QUOTES_AUDIT_OLD&lt;/TABLE_NAME&gt;&lt;DATA_FIELD&gt;QUOTES_AUDIT_OLD.ASSIGNED_USER_ID&lt;/DATA_FIELD&gt;&lt;FIELD_NAME&gt;ASSIGNED_USER_ID&lt;/FIELD_NAME&gt;&lt;DATA_TYPE&gt;guid&lt;/DATA_TYPE&gt;&lt;OPERATOR&gt;changed&lt;/OPERATOR&gt;&lt;SEARCH_TEXT&gt;&lt;/SEARCH_TEXT&gt;&lt;/Filter&gt;&lt;Filter&gt;&lt;ID&gt;b0a99f36-9048-4dbe-97b8-9a5caf6a4aaf&lt;/ID&gt;&lt;MODULE&gt;Users&lt;/MODULE&gt;&lt;MODULE_NAME&gt;Users USERS_ASSIGNED_USER_ID&lt;/MODULE_NAME&gt;&lt;TABLE_NAME&gt;USERS_ASSIGNED_USER_ID&lt;/TABLE_NAME&gt;&lt;DATA_FIELD&gt;USERS_ASSIGNED_USER_ID.RECEIVE_NOTIFICATIONS&lt;/DATA_FIELD&gt;&lt;FIELD_NAME&gt;RECEIVE_NOTIFICATIONS&lt;/FIELD_NAME&gt;&lt;DATA_TYPE&gt;bool&lt;/DATA_TYPE&gt;&lt;OPERATOR&gt;equals&lt;/OPERATOR&gt;&lt;SEARCH_TEXT&gt;1&lt;/SEARCH_TEXT&gt;&lt;SEARCH_TEXT_VALUES&gt;1&lt;/SEARCH_TEXT_VALUES&gt;&lt;/Filter&gt;&lt;Filter&gt;&lt;ID&gt;69eaf69b-f45f-4702-aac2-9a14aa264250&lt;/ID&gt;&lt;MODULE&gt;Users&lt;/MODULE&gt;&lt;MODULE_NAME&gt;Users USERS_ASSIGNED_USER_ID&lt;/MODULE_NAME&gt;&lt;TABLE_NAME&gt;USERS_ASSIGNED_USER_ID&lt;/TABLE_NAME&gt;&lt;DATA_FIELD&gt;USERS_ASSIGNED_USER_ID.EMAIL1&lt;/DATA_FIELD&gt;&lt;FIELD_NAME&gt;EMAIL1&lt;/FIELD_NAME&gt;&lt;DATA_TYPE&gt;string&lt;/DATA_TYPE&gt;&lt;OPERATOR&gt;not_empty&lt;/OPERATOR&gt;&lt;SEARCH_TEXT&gt;&lt;/SEARCH_TEXT&gt;&lt;/Filter&gt;&lt;/Filters&gt;</Value></CustomProperty></CustomProperties><DataSets><DataSet><Query><CommandText>select *    from            QUOTES_AUDIT          QUOTES    left outer join vwUSERS                     USERS_ASSIGNED_USER_ID                 on USERS_ASSIGNED_USER_ID.ID = QUOTES.ASSIGNED_USER_ID    left outer join QUOTES_AUDIT              QUOTES_AUDIT_OLD                 on QUOTES_AUDIT_OLD.ID       = QUOTES.ID                and QUOTES_AUDIT_OLD.AUDIT_VERSION = (select max(QUOTES_AUDIT.AUDIT_VERSION)                                                    from QUOTES_AUDIT                                                   where QUOTES_AUDIT.ID            =  QUOTES.ID                                                     and QUOTES_AUDIT.AUDIT_VERSION &lt;  QUOTES.AUDIT_VERSION                                                     and QUOTES_AUDIT.AUDIT_TOKEN   &lt;&gt; QUOTES.AUDIT_TOKEN                                                 )   where QUOTES.AUDIT_ID = @AUDIT_ID     and QUOTES.DELETED  = 0     and (QUOTES_AUDIT_OLD.AUDIT_ID is null      or (not(QUOTES.ASSIGNED_USER_ID            is null     and QUOTES_AUDIT_OLD.ASSIGNED_USER_ID            is null    ) and (QUOTES.ASSIGNED_USER_ID            &lt;&gt; QUOTES_AUDIT_OLD.ASSIGNED_USER_ID            or QUOTES.ASSIGNED_USER_ID is null or QUOTES_AUDIT_OLD.ASSIGNED_USER_ID is null)))     and USERS_ASSIGNED_USER_ID.RECEIVE_NOTIFICATIONS = 1     and USERS_ASSIGNED_USER_ID.EMAIL1 is not null  </CommandText></Query><Fields></Fields></DataSet></DataSets></Report>', null;

	exec dbo.spWORKFLOWS_UpdateXOML @ID, null, 0, '<crm:SplendidSequentialWorkflow x:Name="Workflow1" WORKFLOW_ID="2D1019C7-BF57-40E8-878D-4A0D2BC7AF9B" xmlns:crm="clr-namespace:SplendidCRM;Assembly=SplendidCRM" xmlns:x="http://schemas.microsoft.com/winfx/2006/xaml" xmlns="http://schemas.microsoft.com/winfx/2006/xaml/workflow">
