			<CodeActivity x:Name="Tasks1_LoadByAUDIT_ID" ExecuteCode="{ActivityBind Tasks1,Path=LoadByAUDIT_ID}" />
		</crm:TaskActivity>
		<crm:AlertActivity x:Name="Alert1" SUBJECT="SplendidCRM Task - $task_name" ALERT_TYPE="Notification" ALERT_TEXT="$task_modified_by has assigned an Task to $task_assigned_to.&#xD;&#xA;&#xD;&#xA;Name: $task_name&#xD;&#xA;&#xD;&#xA;Priority: $task_priority&#xD;&#xA;&#xD;&#xA;Due Date: $task_date_due&#xD;&#xA;&#xD;&#xA;Status: $task_status&#xD;&#xA;&#xD;&#xA;Description: $task_description&#xD;&#xA;&#xD;&#xA;You may review this Task at:&#xD;&#xA;&lt;a href=&quot;$view_url&quot;&gt;$view_url&lt;/a&gt;&#xD;&#xA;&#xD;&#xA;" SOURCE_TYPE="normal message" CUSTOM_TEMPLATE_ID="00000000-0000-0000-0000-000000000000" PARENT_TYPE="Tasks" PARENT_ACTIVITY="Tasks1" WORKFLOW_ID="{ActivityBind Workflow1,Path=WORKFLOW_ID}">
			<crm:WorkflowLogActivity x:Name="Alert1_Log" MODULE_TABLE="EMAILS" WORKFLOW_ID="{ActivityBind Workflow1,Path=WORKFLOW_ID}" />
			<crm:AddRecipientActivity x:Name="Alert1_Tasks1_AssignedUserID" ALERT_NAME="Alert1" RECIPIENT_NAME="" RECIPIENT_ID="{ActivityBind Tasks1,Path=ASSIGNED_USER_ID}" RECIPIENT_TYPE="User" SEND_TYPE="to" />

			<CodeActivity x:Name="Alert1_Send" ExecuteCode="{ActivityBind Alert1,Path=Send}" />
		</crm:AlertActivity>
	</SequenceActivity>
</crm:SplendidSequentialWorkflow>';

